// Parse issue content and extract implementation tasks
const issue = $input.first().json.issue;
const issueBody = issue.body;
const issueTitle = issue.title;
const issueNumber = issue.number;

// Extract task categories based on title emoji
const getIssueCategory = (title) => {
  if (title.includes('ðŸ”’')) return 'security';
  if (title.includes('ðŸ—ï¸')) return 'architecture';
  if (title.includes('ðŸ§ª')) return 'testing';
  if (title.includes('ðŸš€')) return 'devops';
  if (title.includes('âš¡')) return 'performance';
  if (title.includes('ðŸ› ï¸')) return 'error-handling';
  if (title.includes('ðŸ¦')) return 'compliance';
  if (title.includes('ðŸŒ')) return 'device-integration';
  if (title.includes('ðŸ“–')) return 'documentation';
  if (title.includes('ðŸ”§')) return 'code-quality';
  return 'general';
};

// Extract checkboxes from issue body
const extractTasks = (body) => {
  const checkboxRegex = /- \[ \] (.+)/g;
  const tasks = [];
  let match;

  while ((match = checkboxRegex.exec(body)) !== null) {
    tasks.push({
      task: match[1].trim(),
      completed: false,
      priority: determinePriority(match[1])
    });
  }

  return tasks;
};

// Determine task priority based on keywords
const determinePriority = (task) => {
  const criticalKeywords = ['security', 'vulnerability', 'critical', 'authentication', 'encryption'];
  const highKeywords = ['performance', 'database', 'api', 'error handling'];
  const mediumKeywords = ['testing', 'documentation', 'logging'];

  const taskLower = task.toLowerCase();

  if (criticalKeywords.some(keyword => taskLower.includes(keyword))) {
    return 'critical';
  }
  if (highKeywords.some(keyword => taskLower.includes(keyword))) {
    return 'high';
  }
  if (mediumKeywords.some(keyword => taskLower.includes(keyword))) {
    return 'medium';
  }
  return 'low';
};

// Extract code blocks for implementation
const extractCodeBlocks = (body) => {
  const codeBlockRegex = /```(\w+)?\n([\s\S]*?)```/g;
  const codeBlocks = [];
  let match;

  while ((match = codeBlockRegex.exec(body)) !== null) {
    codeBlocks.push({
      language: match[1] || 'text',
      code: match[2].trim()
    });
  }

  return codeBlocks;
};

const category = getIssueCategory(issueTitle);
const tasks = extractTasks(issueBody);
const codeBlocks = extractCodeBlocks(issueBody);

return {
  issueNumber,
  issueTitle,
  category,
  tasks: tasks.filter(task => task.priority === 'critical' || task.priority === 'high'), // Start with high priority
  codeBlocks,
  repositoryUrl: $input.first().json.repository.clone_url,
  repositoryName: $input.first().json.repository.name,
  repositoryOwner: $input.first().json.repository.owner.login
};

// Implement security-related tasks
const task = $input.first().json.currentTask;
const taskDescription = task.task;

// Security implementation logic
const implementSecurityTask = (taskDesc) => {
  const implementations = [];

  // API Key Security
  if (taskDesc.includes('API key') || taskDesc.includes('environment variable')) {
    implementations.push({
      file: '.env.example',
      action: 'update',
      content: `# Security Configuration
STRIPE_SECRET_KEY=sk_test_your_stripe_secret_key_here
STRIPE_PUBLISHABLE_KEY=pk_test_your_stripe_publishable_key_here
JWT_SECRET=your_jwt_secret_here
ENCRYPTION_KEY=your_32_character_encryption_key
API_KEY_SALT=your_api_key_salt_here

# Database
DATABASE_URL=postgresql://user:password@localhost:5432/upp
REDIS_URL=redis://localhost:6379

# Server Configuration
PORT=3000
NODE_ENV=development
FRONTEND_URL=http://localhost:3000

# Logging
LOG_LEVEL=info`
    });

    implementations.push({
      file: 'src/config/environment.ts',
      action: 'create',
      content: `import { z } from 'zod';

const EnvironmentSchema = z.object({
  NODE_ENV: z.enum(['development', 'staging', 'production']),
  PORT: z.coerce.number().default(3000),
  STRIPE_SECRET_KEY: z.string().min(1),
  STRIPE_PUBLISHABLE_KEY: z.string().min(1),
  JWT_SECRET: z.string().min(32),
  ENCRYPTION_KEY: z.string().length(32),
  DATABASE_URL: z.string().url(),
  REDIS_URL: z.string().url(),
  LOG_LEVEL: z.enum(['error', 'warn', 'info', 'debug']).default('info')
});

export const env = EnvironmentSchema.parse(process.env);
export type Environment = z.infer<typeof EnvironmentSchema>;`
    });
  }

  // Request Validation
  if (taskDesc.includes('request validation') || taskDesc.includes('Zod')) {
    implementations.push({
      file: 'src/middleware/validation.ts',
      action: 'create',
      content: `import { Request, Response, NextFunction } from 'express';
import { z } from 'zod';

export const validateRequest = (schema: z.ZodSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      schema.parse(req.body);
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          success: false,
          error: {
            code: 'VALIDATION_ERROR',
            message: 'Invalid request data',
            details: error.errors
          }
        });
      }
      next(error);
    }
  };
};

// Payment request validation schema
export const PaymentRequestSchema = z.object({
  amount: z.number().positive(),
  deviceType: z.string().min(1),
  deviceId: z.string().min(1),
  description: z.string().min(1),
  customerEmail: z.string().email().optional(),
  metadata: z.record(z.any()).optional()
});`
    });
  }

  // Authentication Middleware
  if (taskDesc.includes('authentication') || taskDesc.includes('JWT')) {
    implementations.push({
      file: 'src/middleware/auth.ts',
      action: 'create',
      content: `import { Request, Response, NextFunction } from 'express';
import jwt from 'jsonwebtoken';
import { env } from '../config/environment';

export interface AuthenticatedRequest extends Request {
  user?: {
    id: string;
    email: string;
    role: string;
  };
}

export const authenticateToken = (req: AuthenticatedRequest, res: Response, next: NextFunction) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({
      success: false,
      error: {
        code: 'AUTHENTICATION_REQUIRED',
        message: 'Access token is required'
      }
    });
  }

  jwt.verify(token, env.JWT_SECRET, (err, user) => {
    if (err) {
      return res.status(403).json({
        success: false,
        error: {
          code: 'INVALID_TOKEN',
          message: 'Invalid or expired token'
        }
      });
    }
    req.user = user as any;
    next();
  });
};`
    });
  }

  // Rate Limiting
  if (taskDesc.includes('rate limiting')) {
    implementations.push({
      file: 'src/middleware/rateLimiting.ts',
      action: 'create',
      content: `import rateLimit from 'express-rate-limit';
import RedisStore from 'rate-limit-redis';
import Redis from 'ioredis';
import { env } from '../config/environment';

const redis = new Redis(env.REDIS_URL);

// General API rate limiting
export const generalLimiter = rateLimit({
  store: new RedisStore({
    sendCommand: (...args: string[]) => redis.call(...args),
  }),
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // limit each IP to 100 requests per windowMs
  message: {
    success: false,
    error: {
      code: 'RATE_LIMIT_EXCEEDED',
      message: 'Too many requests, please try again later'
    }
  }
});

// Payment endpoint rate limiting
export const paymentLimiter = rateLimit({
  store: new RedisStore({
    sendCommand: (...args: string[]) => redis.call(...args),
  }),
  windowMs: 60 * 1000, // 1 minute
  max: 10, // limit each IP to 10 payment requests per minute
  message: {
    success: false,
    error: {
      code: 'PAYMENT_RATE_LIMIT_EXCEEDED',
      message: 'Too many payment requests, please try again later'
    }
  }
});`
    });
  }

  return implementations;
};

const implementations = implementSecurityTask(taskDescription);

return { implementations, taskCompleted: true, nextTaskIndex: $input.first().json.taskIndex + 1 };

// Implement architecture-related tasks
const task = $input.first().json.currentTask;
const taskDescription = task.task;

// Architecture implementation logic
const implementArchitectureTask = (taskDesc) => {
  const implementations = [];

  // Database Setup
  if (taskDesc.includes('PostgreSQL') || taskDesc.includes('database')) {
    implementations.push({
      file: 'src/database/connection.ts',
      action: 'create',
      content: `import { Pool } from 'pg';
import { env } from '../config/environment';

class DatabaseConnection {
  private pool: Pool;

  constructor() {
    this.pool = new Pool({
      connectionString: env.DATABASE_URL,
      max: 20,
      idleTimeoutMillis: 30000,
      connectionTimeoutMillis: 2000,
    });
  }

  async query(text: string, params?: any[]): Promise<any> {
    const start = Date.now();
    const client = await this.pool.connect();

    try {
      const result = await client.query(text, params);
      const duration = Date.now() - start;
      console.log('Executed query', { text, duration, rows: result.rowCount });
      return result;
    } finally {
      client.release();
    }
  }

  async transaction(callback: (client: any) => Promise<any>): Promise<any> {
    const client = await this.pool.connect();

    try {
      await client.query('BEGIN');
      const result = await callback(client);
      await client.query('COMMIT');
      return result;
    } catch (error) {
      await client.query('ROLLBACK');
      throw error;
    } finally {
      client.release();
    }
  }

  async close(): Promise<void> {
    await this.pool.end();
  }
}

export const db = new DatabaseConnection();`
    });

    implementations.push({
      file: 'migrations/001_initial_schema.sql',
      action: 'create',
      content: `-- UPP Database Schema

-- Users table
CREATE TABLE users (
  id SERIAL PRIMARY KEY,
  email VARCHAR(255) UNIQUE NOT NULL,
  password_hash VARCHAR(255) NOT NULL,
  role VARCHAR(50) DEFAULT 'user',
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Devices table
CREATE TABLE devices (
  id VARCHAR(255) PRIMARY KEY,
  device_type VARCHAR(100) NOT NULL,
  fingerprint VARCHAR(255) UNIQUE NOT NULL,
  capabilities JSONB NOT NULL,
  security_context JSONB NOT NULL,
  user_id INTEGER REFERENCES users(id),
  status VARCHAR(50) DEFAULT 'active',
  last_seen TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Transactions table
CREATE TABLE transactions (
  id VARCHAR(255) PRIMARY KEY,
  device_id VARCHAR(255) REFERENCES devices(id),
  user_id INTEGER REFERENCES users(id),
  amount DECIMAL(10,2) NOT NULL,
  currency VARCHAR(3) DEFAULT 'USD',
  status VARCHAR(50) NOT NULL,
  payment_intent_id VARCHAR(255),
  merchant_id VARCHAR(255),
  description TEXT,
  metadata JSONB,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
  updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Audit logs table
CREATE TABLE audit_logs (
  id SERIAL PRIMARY KEY,
  user_id INTEGER REFERENCES users(id),
  device_id VARCHAR(255) REFERENCES devices(id),
  action VARCHAR(100) NOT NULL,
  resource VARCHAR(100) NOT NULL,
  result VARCHAR(20) NOT NULL,
  ip_address INET,
  user_agent TEXT,
  correlation_id VARCHAR(255),
  metadata JSONB,
  created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Indexes for performance
CREATE INDEX idx_devices_user_id ON devices(user_id);
CREATE INDEX idx_devices_device_type ON devices(device_type);
CREATE INDEX idx_transactions_device_id ON transactions(device_id);
CREATE INDEX idx_transactions_user_id ON transactions(user_id);
CREATE INDEX idx_transactions_status ON transactions(status);
CREATE INDEX idx_transactions_created_at ON transactions(created_at);
CREATE INDEX idx_audit_logs_user_id ON audit_logs(user_id);
CREATE INDEX idx_audit_logs_created_at ON audit_logs(created_at);`
    });
  }

  // Redis Setup
  if (taskDesc.includes('Redis') || taskDesc.includes('cache')) {
    implementations.push({
      file: 'src/cache/redis.ts',
      action: 'create',
      content: `import Redis from 'ioredis';
import { env } from '../config/environment';

class CacheService {
  private redis: Redis;

  constructor() {
    this.redis = new Redis(env.REDIS_URL, {
      retryDelayOnFailover: 100,
      maxRetriesPerRequest: 3,
      lazyConnect: true
    });

    this.redis.on('error', (error) => {
      console.error('Redis connection error:', error);
    });

    this.redis.on('connect', () => {
      console.log('Redis connected successfully');
    });
  }

  async get(key: string): Promise<string | null> {
    try {
      return await this.redis.get(key);
    } catch (error) {
      console.error('Redis GET error:', error);
      return null;
    }
  }

  async set(key: string, value: string, ttlSeconds?: number): Promise<boolean> {
    try {
      if (ttlSeconds) {
        await this.redis.setex(key, ttlSeconds, value);
      } else {
        await this.redis.set(key, value);
      }
      return true;
    } catch (error) {
      console.error('Redis SET error:', error);
      return false;
    }
  }

  async del(key: string): Promise<boolean> {
    try {
      await this.redis.del(key);
      return true;
    } catch (error) {
      console.error('Redis DEL error:', error);
      return false;
    }
  }

  async exists(key: string): Promise<boolean> {
    try {
      const result = await this.redis.exists(key);
      return result === 1;
    } catch (error) {
      console.error('Redis EXISTS error:', error);
      return false;
    }
  }

  async disconnect(): Promise<void> {
    await this.redis.quit();
  }
}

export const cache = new CacheService();`
    });
  }

  // Microservices Structure
  if (taskDesc.includes('microservices') || taskDesc.includes('service separation')) {
    implementations.push({
      file: 'src/services/PaymentService.ts',
      action: 'create',
      content: `import { PaymentRequest, PaymentResult } from '../modules/universal-payment-protocol/core/types';
import { UPPStripeProcessor } from '../../server/stripe-integration';
import { db } from '../database/connection';
import { cache } from '../cache/redis';

export class PaymentService {
  private stripeProcessor: UPPStripeProcessor;

  constructor() {
    this.stripeProcessor = new UPPStripeProcessor();
  }

  async processPayment(request: PaymentRequest): Promise<PaymentResult> {
    // Check cache for duplicate transactions
    const cacheKey = \`payment_\${request.merchant_id}_\${request.amount}_\${Date.now()}\`;
    const cached = await cache.get(cacheKey);

    if (cached) {
      return JSON.parse(cached);
    }

    try {
      // Process payment through Stripe
      const result = await this.stripeProcessor.processPayment(request);

      // Store transaction in database
      await db.query(
        \`INSERT INTO transactions (id, amount, currency, status, payment_intent_id, merchant_id, description, metadata) 
         VALUES ($1, $2, $3, $4, $5, $6, $7, $8)\`,
        [
          result.transaction_id,
          request.amount,
          request.currency,
          result.status,
          result.transaction_id,
          request.merchant_id,
          request.description,
          JSON.stringify(request.metadata)
        ]
      );

      // Cache successful result
      if (result.success) {
        await cache.set(cacheKey, JSON.stringify(result), 300); // 5 minutes
      }

      return result;
    } catch (error) {
      console.error('Payment processing error:', error);
      throw error;
    }
  }

  async getTransactionStatus(transactionId: string): Promise<any> {
    const result = await db.query(
      'SELECT * FROM transactions WHERE id = $1',
      [transactionId]
    );

    return result.rows[0] || null;
  }

  async getTransactionHistory(userId: number, limit: number = 50): Promise<any[]> {
    const result = await db.query(
      'SELECT * FROM transactions WHERE user_id = $1 ORDER BY created_at DESC LIMIT $2',
      [userId, limit]
    );

    return result.rows;
  }
}`
    });
  }

  return implementations;
};

const implementations = implementArchitectureTask(taskDescription);

return { implementations, taskCompleted: true, nextTaskIndex: $input.first().json.taskIndex + 1 };

// Implement testing, DevOps, or other category tasks
const task = $input.first().json.currentTask;
const taskDescription = task.task;
const category = $input.first().json.category;

const implementGenericTask = (taskDesc, cat) => {
  const implementations = [];

  // Testing Setup
  if (cat === 'testing' || taskDesc.includes('test')) {
    implementations.push({
      file: 'vitest.config.ts',
      action: 'create',
      content: `import { defineConfig } from 'vitest/config';
