  CMD node healthcheck.js

ENTRYPOINT ["dumb-init", "--"]
CMD ["node", "dist/index.js"]`
    });

    implementations.push({
      file: 'docker-compose.yml',
      action: 'create',
      content: `version: '3.8'

services:
